contracts/Direct.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

contract FiatBridge is ReentrancyGuard, Ownable, Pausable {
    // Structs
    struct LockPermission {
        uint256 maxAmount;
        uint256 expiryTime;
        bool isActive;
    }

    struct Transaction {
        address user;           // Added user field to track transaction owner
        address token;
        uint256 amount;
        uint256 amountSpent;   // Added to track how much was actually spent
        uint256 lockTimestamp;
        uint256 unlockTimestamp;
        bool isCompleted;
        bool isRefunded;        // Added to track refund status
    }

    // State variables
    mapping(address => mapping(address => LockPermission)) public userPermissions;
    mapping(bytes32 => Transaction) public transactions;
    mapping(address => bool) public supportedTokens;
    mapping(address => uint256) public collectedFees; // Track fees per token
    uint256 public spreadFeePercentage;
    uint256 public constant MAX_FEE = 500;

    // Events
    event PermissionGranted(address indexed user, address indexed token, uint256 maxAmount, uint256 expiryTime);
    event TransactionInitiated(bytes32 indexed txId, address indexed user, uint256 amount);
    event TransactionCompleted(bytes32 indexed txId, uint256 amountSpent);
    event TransactionRefunded(bytes32 indexed txId, uint256 amountRefunded);
    event TokenAdded(address indexed token);
    event TokenRemoved(address indexed token);
    event FeesWithdrawn(address indexed token, uint256 amount);

    constructor(uint256 _spreadFeePercentage) Ownable(msg.sender) {
        require(_spreadFeePercentage <= MAX_FEE, "Fee too high");
        spreadFeePercentage = _spreadFeePercentage;
    }

    function grantPermission(address token, uint256 maxAmount, uint256 duration) 
        external
        whenNotPaused
    {
        require(supportedTokens[token], "Token not supported");
        require(duration <= 365 days, "Duration too long");

        userPermissions[msg.sender][token] = LockPermission({
            maxAmount: maxAmount,
            expiryTime: block.timestamp + duration,
            isActive: true
        });

        emit PermissionGranted(msg.sender, token, maxAmount, block.timestamp + duration);
    }

    function initiateFiatTransaction(
        address token,
        uint256 amount,
        uint256 lockDuration
    )
        external
        nonReentrant
        whenNotPaused
        returns (bytes32 txId)
    {
        LockPermission storage permission = userPermissions[msg.sender][token];
        require(permission.isActive, "No active permission");
        require(block.timestamp < permission.expiryTime, "Permission expired");
        require(amount > 0, "Amount must be greater than zero");
        require(amount <= permission.maxAmount, "Amount exceeds limit");

        uint256 feeAmount = (amount * spreadFeePercentage) / 10000;
        uint256 totalAmount = amount + feeAmount;

        IERC20 tokenContract = IERC20(token);
        require(
            tokenContract.transferFrom(msg.sender, address(this), totalAmount),
            "Transfer failed"
        );

        // Update collected fees
        collectedFees[token] += feeAmount;

        txId = keccak256(abi.encodePacked(
            msg.sender,
            token,
            amount,
            block.timestamp
        ));

        transactions[txId] = Transaction({
            user: msg.sender,
            token: token,
            amount: amount,
            amountSpent: 0,
            lockTimestamp: block.timestamp,
            unlockTimestamp: block.timestamp + lockDuration,
            isCompleted: false,
            isRefunded: false
        });

        emit TransactionInitiated(txId, msg.sender, amount);
        return txId;
    }

    function completeTransaction(bytes32 txId, uint256 amountSpent)
        external
        onlyOwner
        nonReentrant
    {
        Transaction storage txn = transactions[txId];
        require(!txn.isCompleted && !txn.isRefunded, "Transaction already processed");
        require(block.timestamp <= txn.unlockTimestamp, "Lock expired");
        require(amountSpent <= txn.amount, "Amount spent exceeds locked amount");

        txn.amountSpent = amountSpent;
        txn.isCompleted = true;

        emit TransactionCompleted(txId, amountSpent);

        // If not all funds were spent, refund the remainder
        if (amountSpent < txn.amount) {
            uint256 refundAmount = txn.amount - amountSpent;
            IERC20(txn.token).transfer(txn.user, refundAmount);
            emit TransactionRefunded(txId, refundAmount);
        }
    }

    function claimExpiredLock(bytes32 txId)
        external
        nonReentrant
    {
        Transaction storage txn = transactions[txId];
        require(msg.sender == txn.user, "Not transaction owner");
        require(!txn.isCompleted && !txn.isRefunded, "Transaction already processed");
        require(block.timestamp > txn.unlockTimestamp, "Lock not expired");

        txn.isRefunded = true;
        IERC20(txn.token).transfer(txn.user, txn.amount);

        emit TransactionRefunded(txId, txn.amount);
    }

    function withdrawFees(address token, uint256 amount)
        external
        onlyOwner
        nonReentrant
    {
        require(amount <= collectedFees[token], "Insufficient collected fees");

        collectedFees[token] -= amount;
        IERC20(token).transfer(owner(), amount);

        emit FeesWithdrawn(token, amount);
    }

    // Admin functions
    function addSupportedToken(address token) external onlyOwner {
        supportedTokens[token] = true;
        emit TokenAdded(token);
    }

    function removeSupportedToken(address token) external onlyOwner {
        supportedTokens[token] = false;
        emit TokenRemoved(token);
    }

    function updateSpreadFee(uint256 newFee) external onlyOwner {
        require(newFee <= MAX_FEE, "Fee too high");
        spreadFeePercentage = newFee;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

